      $options{request}->{port_force} = $self->get_port();
  
      $options{request}->{headers} = {};
      if (defined($options{request}->{header})) {
          foreach (@{$options{request}->{header}}) {
              if (/^(.*?):(.*)/) {
                  $options{request}->{headers}->{$1} = $2;
