                          push @{$options{request}->{$method . '_params'}->{$key}}, $value;
                      } else {
                          $options{request}->{$method . '_params'}->{$key} = $value;
                      }
                  }
              }
          }
      }
      
      $self->{'backend_' . $self->{http_backend}}->check_options(%options);
  }
  
  sub get_port {
