$fatpacked{"cloud/azure/management/monitor/mode/discovery.pm"} = '#line '.(1+__LINE__).' "'.__FILE__."\"\n".<<'CLOUD_AZURE_MANAGEMENT_MONITOR_MODE_DISCOVERY';
