          $self->{global}->{total_nodes}++;
      }
  
      if (scalar(keys %{$self->{nodes}}) <= 0) {
          $self->{output}->add_option_msg(short_msg => 'No cluster nodes found');
          $self->{output}->option_exit();
      }
