$fatpacked{"centreon/common/fortinet/fortigate/mode/signatures.pm"} = '#line '.(1+__LINE__).' "'.__FILE__."\"\n".<<'CENTREON_COMMON_FORTINET_FORTIGATE_MODE_SIGNATURES';
  #
