      $options{options}->add_options(arguments => {
      });
      
      return $self;
  }
  
  sub manage_selection {
      my ($self, %options) = @_;    
  
      my $query = q{
          SELECT SUM(gets), SUM(gets-getmisses) FROM v$rowcache
      };
  
