  package database::oracle::mode::sessionusage;
  
  use base qw(centreon::plugins::mode);
  
  use strict;
  use warnings;
  
  sub new {
      my ($class, %options) = @_;
      my $self = $class->SUPER::new(package => __PACKAGE__, %options);
      bless $self, $class;
  
      $options{options}->add_options(arguments => {
          "warning:s"               => { name => 'warning', },
          "critical:s"              => { name => 'critical', },
      });
  
      return $self;
  }
  
  sub check_options {
      my ($self, %options) = @_;
      $self->SUPER::init(%options);
  
      if (($self->{perfdata}->threshold_validate(label => 'warning', value => $self->{option_results}->{warning})) == 0) {
          $self->{output}->add_option_msg(short_msg => "Wrong warning threshold '" . $self->{option_results}->{warning} . "'.");
          $self->{output}->option_exit();
      }
      if (($self->{perfdata}->threshold_validate(label => 'critical', value => $self->{option_results}->{critical})) == 0) {
          $self->{output}->add_option_msg(short_msg => "Wrong critical threshold '" . $self->{option_results}->{critical} . "'.");
          $self->{output}->option_exit();
      }
  }
  
  sub run {
      my ($self, %options) = @_;
      # $options{sql} = sqlmode object
      $self->{sql} = $options{sql};
  
      $self->{sql}->connect();
      $self->{sql}->query(query => q{SELECT current_utilization/limit_value*100 FROM v$resource_limit WHERE resource_name = 'sessions'});
      my $session = $self->{sql}->fetchrow_array();
      $self->{sql}->disconnect();
  
      $session =~ s/,/./g;
      my $exit_code = $self->{perfdata}->threshold_check(value => $session, threshold => [ { label => 'critical', exit_litteral => 'critical' }, { label => 'warning', exit_litteral => 'warning' } ]);
      $self->{output}->output_add(severity => $exit_code,
                                    short_msg => sprintf("%.2f%% of session resources used.", $session));
      $self->{output}->perfdata_add(label => 'session_used', unit => '%',
                                    value => sprintf("%.2f", $session),
                                    warning => $self->{perfdata}->get_perfdata_for_output(label => 'warning'),
                                    critical => $self->{perfdata}->get_perfdata_for_output(label => 'critical'),
                                    min => 0, max => 100);
  
      $self->{output}->display();
      $self->{output}->exit();
  }
  
  1;
  
  
  =head1 MODE
  
  Check Oracle session used.
  
  =over 8
  
  =item B<--warning>
  
  Threshold warning.
  
  =item B<--critical>
  
  Threshold critical.
  
  =back
  
  =cut
DATABASE_ORACLE_MODE_SESSIONUSAGE

